nav {
	background-color: $white;
	position: fixed;
	display: flex;
	top: 0;
	left: 0;
	z-index: 999;
	width: 100vw;
	padding: 1.7em 2em;
	height: 1.5em;

	@include ms {
		padding: 1.7em 1em;
	}

	h2 {
		font-family: $font;
		font-weight: $bold;
	}
}

#toggle {
	position: relative;
	cursor: pointer;
	display: flex;
	justify-content: center;
	align-items: center;
	margin-left: auto;
	margin-right: 5em;
	font-family: $font;

	@include ms {
		margin-right: 2em;
	}

	&::before {
		content: 'M';
		font-size: 1.4em;
		position: absolute;
		left: -1em;
		margin-top: .1em;
		opacity: .5;

		@include ms {
			display: none;
		}
	}

	&::after {
		content: 'NU';
		font-size: 1.4em;
		position: absolute;
		right: -1.6em;
		margin-top: .1em;
		opacity: .5;
		@include ms {
			display: none;
		}
	}

	.button_container {
		position: relative;
		height: 17px;
		width: 25px;
		transition: opacity .25s ease;
		display: flex;

		span {
			background: #000;
			border: none;
			height: 3px;
			border-radius: 4px;
			width: 100%;
			position: absolute;
			top: 0;
			left: 0;
			transition: transform .3s cubic-bezier(.4, 2.08, .55, .44), opacity .25s ease;
			transform: translate3d(0, 0, 0) rotate(0);
			cursor: pointer;

			@include tb {
				pointer-events: none;
			}

			&:nth-of-type(2) {
				top: 7px;
				transform: scaleX(0.6);
			}
			&:nth-of-type(3) {
				top: 14px;
				transform: scaleX(0.8);
			}
		}
		&:hover span {
			transform: scaleX(1);
		}

		&.menu-open span:nth-of-type(1) {
			-webkit-font-smoothing: antialiased;
			-webkit-tap-highlight-color: transparent;
			background-color: #444;
			border-radius: 4px;
			border-style: none;
			cursor: pointer;
			height: 3px;
			left: 0;
			margin: 0;
			outline: none;
			position: absolute;
			text-rendering: optimizelegibility;
			top: 0;
			transform: translate3d(0, 7px, 0) rotate(45deg);
			transition-delay: 0s, 0s;
			transition-duration: .3s, .25s;
			transition-property: transform, opacity;
			transition-timing-function: cubic-bezier(.4, 2.08, .55, .44), ease;
			width: 100%;
		}

		&.menu-open span:nth-of-type(2) {
			-webkit-font-smoothing: antialiased;
			-webkit-tap-highlight-color: transparent;
			background-color: #fff;
			border-radius: 4px;
			border-style: none;
			cursor: pointer;
			height: 3px;
			left: 0;
			margin: 0;
			outline: none;
			position: absolute;
			text-rendering: optimizelegibility;
			top: 7px;
			transform: scaleX(.6);
			transition-delay: 0s, 0s;
			transition-duration: .3s, .25s;
			transition-property: transform, opacity;
			transition-timing-function: cubic-bezier(.4, 2.08, .55, .44), ease;
			width: 100%;
			opacity: 0;
		}

		&.menu-open span:nth-of-type(3) {
			-webkit-font-smoothing: antialiased;
			-webkit-tap-highlight-color: transparent;
			background-color: #444;
			border-radius: 4px;
			border-style: none;
			cursor: pointer;
			height: 3px;
			left: 0;
			margin: 0;
			outline: none;
			position: absolute;
			text-rendering: optimizelegibility;
			top: 0;
			transform: translate3d(0, 7px, 0) rotate(-45deg);
			transition-delay: 0s, 0s;
			transition-duration: .3s, .25s;
			transition-property: transform, opacity;

			width: 100%;
		}
	}
}

ul.menu {
	display: grid;
	position: fixed;
	justify-items: center;
	align-items: center;
	margin-left: auto;
	list-style: none;
	// margin-right: 4em;
	margin-top: 1.5em;
	padding-top: 3em;
	height: 100vh;
	width: 100%;
	background: $white;
	z-index: 99;
	transition: .25s ease-in-out;
	transition-property: transform;
	transition-duration: .5s;
	transition-timing-function: ease-in-out;
	transform: translate(100%, -100%) scale(.4);
text-align: center;
	&.open {
		transform: translate(0%) scale(1);
	}


	li {
		color: $primary-text;
		margin-bottom: 1em;
		font-family: $font;
		font-weight: $bold;
		font-size: $responsive__font;
		--max-font: 50;
		--min-font: 30;

		span {
			background-position: 0 100%;
			transition: background-position 0.5s;

			&:hover {
				background-image: linear-gradient(to right, transparent 50%, #ffc600 50%);
				background-origin: 0;
				background-size: 200% 40%;
				background-repeat: repeat-x;
				background-position: -100% 100%;
				transition: background-position 0.5s;
				cursor: pointer;
			}

			&.active {
				background-image: linear-gradient(to right, transparent 50%, #ffc600 50%);
				background-origin: 0;
				background-size: 200% 40%;
				background-repeat: repeat-x;
				background-position: -100% 100%;
				transition: background-position 0.5s;
			}
		}
	}
}

