#introduction {
	padding-top: 5em;
	width: 100vw - 20vw;
	margin: auto;
	display: grid;
	justify-items: center;
	align-items: center;
	height: auto;
	min-height: 80vh;

	@include tb {
		min-height: 60vh;
	}

	@include ml {
		min-height: 40vh;
	}

	@include mm {
		min-height: 0;
	}

	.content {
		width: 100%;
		margin: auto;
		// letter-spacing: -.3em;

		grid-row: 2;
		line-height: 1.1;

		h1 {
			font-family: $font;
			font-weight: $regular;
			font-size: $responsive__font;
			--max-font: 95;
			--min-font: 45;

			span {
				font-weight: $bold;
			}

			.half-bg {
				background-image: linear-gradient(to right, transparent 50%, #ffc600 50%);
				background-origin: 0;
				background-size: 200% 40%;
				background-repeat: repeat-x;
				background-position: -100% 100%;
				transition: background-position 0.5s;
				font-weight: $regular;
			}
		}
	}
}

.button {
	display: inline-block;
	text-decoration: none;
	position: relative;
	margin-top: 5vw;
	width: calc((var(--min-font) * 1px) + (var(--max-font) - var(--min-font)) * ((100vw - 420px) / (1200 - 420)));
	--max-font: 177;
	--min-font: 100;

	@include lg {
		width: 30%;
	}

	@include ml {
		margin-top: 7vw;
		width: 50%;
	}

	@include ms {
		margin-top: 7vw;
		width: 60%;
	}
}

.button .bottom {
	position: absolute;
	left: 7px;
	top: 7px;
	width: 100%;
	height: 100%;
	background-color: #fdd104;
	display: block;
	-webkit-transition: all .15s ease-out;
	-moz-transition: all .15s ease-out;
	-o-transition: all .15s ease-out;
	transition: all .15s ease-out;
}

.button .top {
	position: relative;
	left: 0;
	top: 0;
	padding: 24px 34px 22px 34px;
	border: 2px solid rgba(black, .5);

	@include ml {
		padding: 14px 14px 12px 14px;
	}
}

.button-dark .top {
	border: 2px solid #fff;
}

.button .top .label {
	font-family: sans-serif;
	font-weight: 600;
	color: black;
	font-size: 12px;
	line-height: 110%;
	letter-spacing: 2px;
	text-align: center;
	text-transform: uppercase;
	-webkit-transition: all .15s ease-out;
	-moz-transition: all .15s ease-out;
	-o-transition: all .15s ease-out;
	transition: all .15s ease-out;
}

.button-dark .top .label {
	color: #fff;
}

.button:hover .bottom {
	left: 0;
	top: 0;
	background-color: #fdd104;
}

.button:hover .top .label {
	color: black;
}

.button-border {
	position: absolute;
	background-color: #fdd104;
	-webkit-transition: all .25s ease-out;
	-moz-transition: all .25s ease-out;
	-o-transition: all .25s ease-out;
	transition: all .25s ease-out;
}

.button:hover .top .button-border-left,
.button:hover .top .button-border-right {
	height: calc(100% + 2px);
}

.button:hover .top .button-border-top,
.button:hover .top .button-border-bottom {
	width: calc(100% + 2px);
}

.button-border-left {
	left: -2px;
	bottom: -2px;
	width: 2px;
	height: 0;
}

.button-border-top {
	left: -2px;
	top: -2px;
	width: 0;
	height: 2px;
}

.button-border-right {
	right: -2px;
	top: -2px;
	width: 2px;
	height: 0;
}

.button-border-bottom {
	right: -2px;
	bottom: -2px;
	width: 0;
	height: 2px;
}

.fa-chevron-down {
	grid-row: 7;
	font-size: 1.8em;
	justify-self: center;
	animation-name: scrollAnimation;
	animation-duration: 2s;
	animation-iteration-count: infinite;
	animation-timing-function: ease-in-out;
	margin-top: 1em;
	@include ml {
display:none;	}
}

@keyframes scrollAnimation {
	50% {
		transform: translateY(-12px);
